import Component from './Component.js';
import Pin from './Pin.js';
import ComponentType from '../ComponentType.js';

/**
 * Ground class
 */
export default class Ground extends Component {

    /**
     * Creates an instance of Ground.
     */
    constructor() {
        super('G');
        this.type = ComponentType.TYPE_GROUND;
        this.controlled = { V: 0 };
        this.dependant = {};
        this.pins = [new Pin(this, 0), new Pin(this, 1)];
    }

    /**
     * Throw error if this is called.
     * This method is implemented to override the parent's.
     */
    stamp(): void {
        throw new TypeError('Ground component does not need to stamp any matrices!');
    }
}

import VoltageSource from './VoltageSource.js';
import CurrentSource from './CurrentSource.js';
import Capacitor from './Capacitor.js';
import Inductor from './Inductor.js';
import Resistor from './Resistor.js';
import Ground from './Ground.js';
import Wire from './Wire.js';

export {
    VoltageSource,
    CurrentSource,
    Capacitor,
    Inductor,
    Resistor,
    Ground,
    Wire,
};

// @flow

import Component from './Component.js';
import Pin from './Pin.js';
import ComponentType from '../ComponentType.js';

/**
 * Utilising Norton Companion Model and Trapezoidal Approximation
 * for Transient Analysis
 */
export default class Inductor extends Component {

    constructor(initInductance: number = 1) {
        super('L');
        this.type = ComponentType.TYPE_INDUCTOR;
        this.controlled = { L: initInductance };
        this.dependant = {
            // Initial state
            I: 0,
            V: 0,
            // Companion model's values
            companionG: undefined,
            companionI: undefined
        };
        this.pins = [new Pin(this, 0), new Pin(this, 1)];
    }

    /**
     * Stamps the Y and J matrices
     * @param {Matrix} matrixY 
     * @param {Matrix} matrixJ
     * @param {Object} simConfig
     */
    stamp(matrixY: Matrix, matrixJ: Matrix, simConfig: Object): void {

        if(this.dependant.companionG == undefined) {
            this.dependant.companionG = simConfig.timestep / (2 * this.controlled.L);
        }
        this.dependant.companionI = this.dependant.I + this.dependant.companionG * this.dependant.V;

        const [from, to] = this.nodes;

        // Stamp the conductance for the companion model's resistor
        matrixY.data[to][to]     += this.dependant.companionG;
        matrixY.data[from][from] += this.dependant.companionG;
        matrixY.data[to][from]   += -this.dependant.companionG;
        matrixY.data[from][to]   += -this.dependant.companionG;

        // Stamp the current for the companion model's current source
        // The curren source is opposite direction of Capacitor's
        matrixJ.data[from][0] += -this.dependant.companionI;
        matrixJ.data[to][0]   += this.dependant.companionI;
    }

    /**
     * Updates this component's dependant values and pins' voltage.
     * Called from CircuitUpdater update() method
     * @param {Object} value 
     */
    update(value: Object): void {
        let [p0, p1] = value.pins;

        this.pins[0].V = p0;
        this.pins[1].V = p1;

        this.dependant.V = p0 - p1;
        this.dependant.I = (this.dependant.V * this.dependant.companionG) + this.dependant.companionI;
    }
}

// @flow

/**
 * Pin class
 */
export default class Pin {
    
	parent : Component
	index : number
	position : number[]|null
	visited : boolean
    V: number

    /**
     * Creates an instance of Pin.
     * @param {Component} parent 
     * @param {number} index 
     */
	constructor(parent: Component, index: number) {
        /**
         * Component that "owns" this pin
         * @type {Component}
         */
		this.parent = parent;
        /** 
         * Index of this pin in the parent
         * @type {number}
        */
		this.index = index;
        /**
         * Position on the board
         * @type {number[][] | null} [position=null]
         */
        this.position = null;
        /**
         * Visited by the Traverser during parsing
         * @type {boolean} [visited=false]
         */
        this.visited = false;
        /**
         * Voltage on this pin
         * @type {number} [V=0]
         */
        this.V = 0;
	}
}

// @flow

import Component from './Component.js';
import Pin from './Pin.js';
import ComponentType from '../ComponentType.js';

/**
 * Resistor class
 */
export default class Resistor extends Component {

    /**
     * Creates an instance of Resistor.
     * @param {number} [initResistance=5] 
     */
    constructor(initResistance: number = 5) {
        super('R');
        this.type = ComponentType.TYPE_RESISTOR;
        this.controlled = { R: initResistance };
        this.dependant = { I: undefined, V: undefined };
        this.pins = [new Pin(this, 0), new Pin(this, 1)];
    }

    /**
     * Stamps the just conductance matrix - matrixY
     * @param {Matrix} matrixY 
     */
    stamp(matrixY: Matrix):void {
        const G = 1 / this.controlled.R, // conductance = 1/resistance
            [from, to] = this.nodes;

        matrixY.data[to][to] += G;
        matrixY.data[from][from] += G;
        matrixY.data[to][from] += -G;
        matrixY.data[from][to] += -G;
    }

    /**
     * Updates this component's dependant values and pins' voltage.
     * Called from CircuitUpdater update() method
     * @param {Object} value 
     */
    update(value: Object): void {
        let [p0, p1] = value.pins;
       
        this.pins[0].V = p0;
        this.pins[1].V = p1;
        
        this.dependant.V = Math.abs(p0 - p1);
        this.dependant.I = this.dependant.V / this.controlled.R;
    }
}

// @flow

import Component from './Component.js';
import Pin from './Pin.js';
import ComponentType from '../ComponentType.js';

/**
 * VoltageSource class
 */
export default class VoltageSource extends Component {

    vSourceNum: number;

    /**
     * Creates an instance of VoltageSource.
     * @param {number} [initVoltage=5]
     * @param {string} [symbol=V]
     */
    constructor(initVoltage: number = 5, symbol: string = 'V') {
        super(symbol);
        this.type = ComponentType.TYPE_SOURCE;
        this.controlled = { V: initVoltage };
        this.dependant = { I: undefined };
        this.pins = [new Pin(this, 0), new Pin(this, 1)];

        /**
         * Denotes the n-th voltage source. This is so that we know
         * which row and col of the Y and J matrices to stamp.
         * @type {number}
         */
        this.vSourceNum = 0;
    }

    /**
     * Stamps the matrices Y and J
     * @param {Matrix} matrixY 
     * @param {Matrix} matrixJ
     */
    stamp(matrixY: Matrix, matrixJ: Matrix): void {
        const [from, to] = this.nodes,
            vSourceIndex = matrixY.data.length - this.vSourceNum;

        matrixY.data[vSourceIndex][from] = matrixY.data[from][vSourceIndex] = 1;
        matrixY.data[vSourceIndex][to] = matrixY.data[to][vSourceIndex] = -1;

        matrixJ.data[vSourceIndex][0] = this.controlled.V
    }

    /**
     * Updates this component's dependant values and pins' voltage.
     * Called from CircuitUpdater update() method
     * @param {Object} value 
     */
    update(value: Object): void {
        this.dependant.I = value.I;
        this.pins[0].V = value.pins[0];
        this.pins[1].V = value.pins[1];
    }
}

