import Component from './Component.js';
import Pin from './Pin.js';
import VoltageSource from './VoltageSource.js';
import ComponentType from '../ComponentType.js';

/**
 * Wire class
 * A zero voltage source
 */
export default class Wire extends VoltageSource {

    /**
     * Creates an instance of Wire.
     */
    constructor() {
        super(0, 'W');
        this.type = ComponentType.TYPE_CONNECTOR;
        this.dependant = { V: undefined, I: undefined };
        this.pins = [new Pin(this, 0), new Pin(this, 1)];
    }

    /**
     * Updates this component's dependant values and pins' voltage.
     * Called from CircuitUpdater update() method
     * @param {Object} value 
     */
    update(value: Object): void {
        super.update(value);
        this.dependant.V = value.pins[0];
    }
}

const ComponentType = {
    TYPE_SOURCE: Symbol('Source'),
    TYPE_RESISTOR: Symbol('Resistor'),
    TYPE_CONNECTOR: Symbol('Connector'),
    TYPE_CAPACITOR: Symbol('Capacitor'),
    TYPE_INDUCTOR: Symbol('Inductor'),
    TYPE_GROUND: Symbol('Ground')
};

export {ComponentType as default};

// @flow

/**
 * Instead of inheriting from Array type, I wrapped an
 * array in Matrix class. Array inheritance is still not
 * supported properly in transpilation. The problem I had 
 * is that the inherited Array will always be returned,
 * rather than the derived's instance.
 * See: https://babeljs.io/docs/usage/caveats/
 * 
 */
export default class Matrix {

    data: number[][];

    /**
     * Creates an instance of Matrix.
     * @param {number} [row=undefined] 
     * @param {number} [col=undefined] 
     */
    constructor(row: ?number = undefined, col: ?number = undefined) {

        /**
         * Representation of the matrix
         * @type {number[][]}
         */
        this.data = [];

        if(Number.isInteger(row)) {

            this.data.length = row;

            if(Number.isInteger(col) || col === undefined || col === null) {

                let colArr = new Array(col || row).fill(0);

                this.data.fill().forEach((r, i, a) => {
                    a[i] = (colArr.slice());
                });

            }
        }
    }

    /**
     * Adds a 0-filled row to the matrix
     * @public
     * @param {number} [amount]
     */
    addRow(amount: ?number): void {
        // If this matrix has at least one row
        if(this.data.length > 0) {
            // Then we will base the length of the first row
            // to create the new ones
            let len = this.data[0].length,
                arr = [];

            arr.length = len;
            arr.fill(0);

            // If amount not set, then run once
            for(let i = 0; i < (Number.isInteger(amount)? amount : 1); ++i) {
                this.data.push(arr.slice()); // slice called to copy array
            }
        }
        else {
            for(let i = 0; i < (Number.isInteger(amount)? amount : 1); ++i) {
                this.data.push([]);
            }
        }
    }

    /**
     * Adds a 0-filled column to the matrix
     * @public
     * @param {number} [amount]
     */
    addCol(amount: ?number): void {
        // If this matrix has at least one row
        if(this.data.length > 0) {
            let newCols = [0];
            if(Number.isInteger(amount) && amount > 0) {
                newCols.length = amount;
                newCols.fill(0);
            }
            this.data.map(function(row) {
                row.push(...newCols);
            });
        }
        else {
            this.data.push([]);
        }
    }

    /**
     * Converts row matrix to column matrix and returns it
     * @static
     * @param {number[]} row
     * 
     * @returns {number[][]}
     */
    static rowToCol(row: number[]): number[][] {
        return row.map(num => [num]);
    }
}

let config = {
    timestep: 5e-6,
    maxTime: 100e-3 
};

export { config as default };

// @flow
import fs from 'fs';

import Board from './Board';
import Traverser from './Traverser';
import Solver from './Solver';
import CircuitUpdater from './CircuitUpdater';
import SimConfig from './SimulationConfig';
import { VoltageSource, CurrentSource, Wire, Inductor, Capacitor, Ground, Resistor } from './Components';

import RLC_circuit from '../circuits/RLC';

let maxIterations = SimConfig.maxTime / SimConfig.timestep,
    time = 0,
    fileData = '',
    solution = [];

// Save references for later use
let B = RLC_circuit.board,
    C = RLC_circuit.components.C;

// Simulation starts here
let nodes = Traverser.assignComponentNodes(B.pins),
    solver = new Solver(SimConfig);

while(maxIterations--) {
    solution = solver.solve(B.components, nodes);

    // Store all the data first, then execute only one file write later
    fileData += `${time}, ${C.dependant.I}, ${C.dependant.V}\n`;

    CircuitUpdater.update(CircuitUpdater.getUpdateObject(nodes, solution));
    
    time += SimConfig.timestep;
}

fs.writeFile('RLC.csv', fileData);

// @flow

import Matrix from './Matrix.js';
import ComponentType from './ComponentType.js';
import Numeric from 'numericjs';

/**
 * Solver class of the simulator.
 * Creates the matrices and initiates the stamping, and solve using NumericJS' LUsolve method.
 */
export default class Solver {

    simConfig: Object;
    LU_Y: Object;

    constructor(simConfig: Object) {
        /**
         * Key-value object that holds the config for this simulation
         * @type {Object}
         */
        this.simConfig = simConfig;

        /**
         * Stores the LU decomposed Y matrix result object from Numeric.LU()
         * @type {Object}
         */
        this.LU_Y = undefined;
    }

    /**
     * Gets the number of voltage sources
     * @param {BoardComponents} components 
     * 
     * @returns {number}
     */
    getNumVSource(components: BoardComponents): number {
        let vSourceCount = 0;
        for(let id in components) {
            if('vSourceNum' in components[id]) {
                ++vSourceCount;
            }
        }
        return vSourceCount;
    }

    /**
     * Stamps Y and J matrices with the values of the components
     * @param {BoardComponents} components 
     * @param {Matrix} Y 
     * @param {Matrix} J 
     */
    stampMatrices(components: BoardComponents, Y: Matrix, J: Matrix): void {

        let vSourceNum = 1;
        for(let id in components) {
            let component = components[id];
            if(component.vSourceNum !== undefined) {
                component.vSourceNum = vSourceNum++;
            }

            if(components[id].type !== ComponentType.TYPE_GROUND) {
                components[id].stamp(Y, J, this.simConfig);
            }
        }
    }

    /**
     * In MNA, we have three matrices Yx = J.
     * This method solves the circuit by calculating the values for
     * matrix x and returns it.
     * @param {BoardComponents} components 
     * @param {Pin[][]} nodes
     * 
     * @returns {number[]} solution
     */
    solve(components: BoardComponents, nodes: Pin[][]): number[] {
        let numNode = nodes.length,
            numVSource = this.getNumVSource(components);

        let Y = new Matrix(numNode + numVSource),
            J = new Matrix(numNode + numVSource, 1);

        this.stampMatrices(components, Y, J);

        if(this.LU_Y == undefined) {

            // Remove the 0-th row and column because we don't need Ground nodes
            Y.data.splice(0,1);
            for(let row in Y.data) {
                Y.data[row] = Y.data[row].splice(1, Y.data[row].length);
            }
            this.LU_Y = Numeric.LU(Y.data);
        }
        // Same as above for matrix J
        J.data.splice(0,1);

        // Start with 0, which is the voltage of the 0th/ground node
        return [0].concat(...Numeric.LUsolve(this.LU_Y, J.data));
    }
}

